mapper.select:
select * from hap_code

	
		and ID = #{id}
	
	
		and CODEVALUE like #{codevalue}
	
	
		and CODETEXT like #{codetext}
	
	
		and CODETYPE like #{codetype}
	
	
		and CODEORDER = #{codeorder}
	
	
		and CREATE_USER_ID = #{createUserId}
	
	
		and UPDATE_USER_ID = #{updateUserId}
	
	
		and ${mywhere}
	


	order by ${orderBy}

select * from hap_code

	
		and ID = #{id}
	
	
		and CODEVALUE like #{codevalue}
	
	
		and CODETEXT like #{codetext}
	
	
		and CODETYPE like #{codetype}
	
	
