mapper.select:
select * from hap_user_account

	
		and ID = #{id}
	
	
		and ENABLE = #{enable}
	
	
		and ACCOUNT like #{account}
	
	
		and PASSWORD like #{password}
	
	
		and ACCOUNT_TYPE = #{accountType}
	
	
		and LASTLOGINIP like #{lastloginip}
	
	
		and PASSQUESTION like #{passquestion}
	
	
		and PASSANSWER like #{passanswer}
	
	
		and CREATE_USER_ID = #{createUserId}
	
	
		and UPDATE_USER_ID = #{updateUserId}
	
	
		and DELFLAG = #{delflag}
	
	
		and CREATEORGID = #{createorgid}
	
	
		and MEMO like #{memo}
	
	
		and TEMP1 like #{temp1}
	
	
		and TEMP2 like #{temp2}
	
	
		and ${mywhere}
	


	order by ${orderBy}

