select a.ID as ID, a.ENABLE as ENABLE, a.ACCOUNT as ACCOUNT, a.PASSWORD as PASSWORD, a.STARTTIME as STARTTIME, a.ENDTIME as ENDTIME, a.ACCOUNT_TYPE as ACCOUNT_TYPE, a.LASTLOGINDATE as LASTLOGINDATE, a.LASTLOGINIP as LASTLOGINIP, a.PASSQUESTION as PASSQUESTION, a.PASSANSWER as PASSANSWER, a.CREATE_TIME as CREATE_TIME, a.CREATE_USER_ID as CREATE_USER_ID, a.UPDATE_USER_ID as UPDATE_USER_ID, a.UPDATE_TIME as UPDATE_TIME, a.DELFLAG as DELFLAG, a.CREATEORGID as CREATEORGID, a.MEMO as MEMO, a.TEMP1 as TEMP1, a.TEMP2 as TEMP2 from hap_user_account a

	
		and ID = #{id}
	
	
		and ENABLE = #{enable}
	
	
		and ACCOUNT like #{account}
	
	
		and PASSWORD like #{password}
	
	
		and ACCOUNT_TYPE = #{accountType}
	
	
		and LASTLOGINIP like #{lastloginip}
	
	
		and PASSQUESTION like #{passquestion}
	
	
		and PASSANSWER like #{passanswer}
	
	
		and CREATE_USER_ID = #{createUserId}
	
	
